import { useState } from "react";
import isUrl from "is-url";
import Select from 'react-select';

function ProjectForm() {
  const [formData, setFormData] = useState({
    projectName: '',
    projectAbstract: '',
    projectUrl: '',
    contacts: [{ name: '', email: '' }],
    projectAreas: [],
    licenses: [],
    guidelinesUrl: ''
  });

  const projectAreaOptions = [
    { value: 'ai', label: 'Artifical Intelligence' }
  ];

  const licenseOptions = [
    { value: 'mit', label: 'MIT License' }
  ];

  const [errors, setErrors] = useState({});

  const [successMessage, setSuccessMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
        ...prevState,
        [name]: value
    }));

    setErrors(prevErrors => ({
      ...prevErrors,
      [name]: validateField(name, value)
  }));
  };

  const handleContactChange = (index, e) => {
    const { name, value } = e.target;
    const newContacts = [...formData.contacts];
    newContacts[index][name] = value;
    setFormData({ ...formData, contacts: newContacts });
  };

  const addContact = () => {
    const newContacts = [...formData.contacts, { name: '', email: '' }];
    if (newContacts.length <= 5) {
      setFormData({ ...formData, contacts: newContacts });
    }
  };

  const removeContact = (index) => {
    const newContacts = [...formData.contacts];
    newContacts.splice(index, 1);
    setFormData({ ...formData, contacts: newContacts });
  };

  const handleSelectChange = (name, selectedOptions) => {
    const values = selectedOptions.map(option => option.value);
    setFormData({ ...formData, [name]: values });
  };

  const validateField = (name, value) => {
    switch (name) {
      case 'projectName':
        if (!value) return 'Please enter a project name';
        break;
      case 'projectAbstract':
        if (!value) return 'Please enter a project description';
        break;
      case 'projectUrl':
        if (!value) return 'Please enter a URL to the project page';
        if (!isUrl(value)) return 'URL is not in a valid format';
        break;
      case 'guidelinesUrl':
        if (!value) return 'Please enter a URL to the project contribution guidelines';
        if (!isUrl(value)) return 'URL is not in a valid format';
        break;
      default:
        break;
    }
    return '';
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    const jsonFormData = JSON.stringify(formData);

    let isValid = true;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!formData.projectName) {
      setErrors(prevErrors => ({ ...prevErrors, projectName: 'Please enter a project name' }));
      isValid = false;
    }
    if (!formData.projectAbstract) {
      setErrors(prevErrors => ({ ...prevErrors, projectAbstract: 'Please enter a brief project abstract' }));
      isValid = false;
    }


    if (isValid) {
      const response = await fetch('http://localhost:3001/submit-form', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: jsonFormData,
      });

      if (response.ok) {
        setSuccessMessage("Project submitted successfully!");
      } else {
        setSuccessMessage("Something went wrong...please try again.");
      }
    } else {
      setSuccessMessage("");
    }
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <label>
          Project Name:
          <input
            type="text"
            name="projectName"
            value={formData.projectName}
            onChange={handleChange}
          />
        </label>

        {errors.projectName && <div className="error">{errors.projectName}</div>}

        <label>
          Project Abstract:
          <input
            type="text"
            name="projectAbstract"
            value={formData.projectAbstract}
            onChange={handleChange}
          />
        </label>

        <label>
          Project URL:
          <input
            type="text"
            name="projectUrl"
            value={formData.projectUrl}
            onChange={handleChange}
          />
        </label>

        <div className="contacts-section">
          {formData.contacts.map((contact, index) => (
            <div key={index} className="contact-input">
              <input
                type="text" 
                name="name" 
                value={contact.name} 
                onChange={(e) => handleContactChange(index, e)} 
                placeholder="Enter contact's name"
              />
              <input
                type="email" 
                name="email" 
                value={contact.email} 
                onChange={(e) => handleContactChange(index, e)} 
                placeholder="Enter contact's email"
              />
              {index > 0 && (
                <button type="button" onClick={() => removeContact(index)}>
                  Remove
                </button>
              )}
            </div>
          ))}

          {formData.contacts.length < 5 && (
            <button type="button" onClick={addContact}>
              Add new Contact
            </button>
          )}
        </div>

        <Select
          isMulti
          name="projectAreas"
          options={projectAreaOptions}
          className="select-container"
          classNamePrefix="select"
          onChange={(selectedOptions) => handleSelectChange('projectAreas', selectedOptions)} 
        />

        <Select
          isMulti
          name="licenses"
          options={licenseOptions}
          className="select-container"
          classNamePrefix="select"
          onChange={(selectedOptions) => handleSelectChange('licenses', selectedOptions)} 
        />

        <label>
          Community Contribution Guidelines URL:
          <input
            type="text"
            name="guidelinesUrl"
            value={formData.guidelinesUrl}
            onChange={handleChange}
          />
        </label>

        <button type="submit">Submit</button>
      </form>

      {successMessage && <p> {successMessage} </p>}
    </div>
  );
}

export default ProjectForm;
