import { useState } from "react";
import isUrl from "is-url";
import { Text } from "react";

function ProjectForm() {
  const [formData, setFormData] = useState(
    { projectName: '', projectDescription: '', email: '', projectUrl: '', projectTopics: [], licenses: []})

  const [urlError, setUrlError] = useState('');
  const [emailError, useEmailError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
        ...prevState,
        [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const jsonFormData = JSON.stringify(formData);

    try {
      let isValid = true;

      const projectUrlCheck = isUrl(formData.projectUrl);
      if (!projectUrlCheck) {
        setUrlError("Project URL is not in a valid format");
        isValid = false;
      }

      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      const emailCheck = emailRegex.test(formData.email);
      if (!emailCheck) {
        setEmailError("Contact email is not a valid email");
        isValid = false;
      }

      if (isValid) {
        const response = await fetch('http://localhost:3001/submit-form', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: jsonFormData,
        });

        if (response.ok) {
          console.log('Data submitted successfully');
        } else {
          console.error('Failed to submit data');
        }
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Project Name:
        <input
          type="text"
          name="projectName"
          value={formData.projectName}
          onChange={handleChange}
        />
      </label>
      <label>
        Project Description:
        <input
          type="text"
          name="projectDescription"
          value={formData.projectDescription}
          onChange={handleChange}
        />
      </label>
      <label>
        Email:
        <input
          type="text"
          name="email"
          value={formData.email}
          onChange={handleChange}
        />
      </label>
      {emailError ? <Text style={{ color: 'red'}}>{emailError}</Text> : null}
      <label>
        Project URL:
        <input
          type="text"
          name="projectUrl"
          value={formData.projectUrl}
          onChange={handleChange}
        />
      </label>
      {urlError ? <Text style={{ color: 'red'}}>{urlError}</Text> : null}
      <button type="submit">Submit</button>
    </form>
  );
}

export default ProjectForm;
